mapper.select:

select u_id, u_status, u_name, u_email, u_pwd, u_gender, u_age, u_pic
from user_table
where u_id = #{uId,jdbcType=INTEGER}

mapper.update:
update user_table
set
  u_pwd = #{uPwd,jdbcType=VARCHAR}
where u_id = #{uId,jdbcType=INTEGER}

 update user_table
set
  u_status=#{status}
where u_id = #{uId}
